else if (code === 404) status = 'Not found';
else if (code >= 400) status = 'Client error';
else if (!$('Set Service Config').first().json.EXPECTED_CONTENT ? false : !body.includes($('Set Service Config').first().json.EXPECTED_CONTENT)) status = 'Unexpected content';

